contracts/CodeDAOToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

contract CodeDAOToken is ERC20, Ownable, Pausable {
    uint256 public constant MAX_SUPPLY = 1000000000 * 10**18;

    mapping(address => bool) public authorizedMinters;
    mapping(address => uint256) public totalContributions;
    mapping(address => uint256) public linesOfCode;

    event TokensEarned(address indexed user, uint256 amount, string reason);
    event MinterAuthorized(address indexed minter);
    event MinterRevoked(address indexed minter);

    constructor() ERC20("CodeDAO Token", "CODE") Ownable(msg.sender) {
        _mint(msg.sender, 100000000 * 10**18);
        authorizedMinters[msg.sender] = true;
    }

    function mintForContribution(
        address user, 
        uint256 amount, 
        uint256 linesWritten,
        string memory reason
    ) external whenNotPaused {
        require(authorizedMinters[msg.sender], "Not authorized to mint");
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");
        require(user != address(0), "Invalid user address");

        totalContributions[user] += amount;
        linesOfCode[user] += linesWritten;

        _mint(user, amount);

        emit TokensEarned(user, amount, reason);
    }

    function authorizeMinter(address minter) external onlyOwner {
        authorizedMinters[minter] = true;
        emit MinterAuthorized(minter);
    }

    function revokeMinter(address minter) external onlyOwner {
        authorizedMinters[minter] = false;
        emit MinterRevoked(minter);
    }

    function getUserStats(address user) external view returns (
        uint256 tokenBalance,
        uint256 totalEarned,
        uint256 totalLines
    ) {
        return (
            balanceOf(user),
            totalContributions[user],
            linesOfCode[user]
        );
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function _update(address from, address to, uint256 value) internal override whenNotPaused {
        super._update(from, to, value);
    }
}

contracts/SimpleDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract CodeDAODistributor is ReentrancyGuard {

    IERC20 public constant CODE_TOKEN = IERC20(0x1F8b43F7aeD0D1b524Ec5b4930C19098E8D4fbD0);
    address public constant REWARDS_POOL = 0x050C7f5aeD5881F3Ce0325638F51f854F680e96F;

    // Anti-gaming protection
    mapping(bytes32 => bool) public usedCodeHashes;
    mapping(address => uint256) public lastClaimTime;
    mapping(address => uint256) public dailyClaimed;
    mapping(address => uint256) public lastDayReset;

    // Rate limits and rewards
    uint256 public constant COOLDOWN = 1 hours;
    uint256 public constant DAILY_LIMIT = 50e18; // 50 CODE per day
    uint256 public constant BASE_RATE = 0.1e18;  // 0.1 CODE per line
    uint256 public constant MIN_CLAIM = 1e18;    // 1 CODE minimum

    // Developer stats
    mapping(address => uint256) public totalLinesSubmitted;
    mapping(address => uint256) public totalRewardsEarned;
    mapping(address => uint256) public totalClaims;

    event RewardClaimed(
        address indexed user, 
        uint256 amount, 
        uint256 lines,
        uint256 functions,
        uint256 classes,
        uint256 tests
    );

    function claimRewards(
        uint256 linesOfCode,
        uint256 functions,
        uint256 classes, 
        uint256 tests,
        uint256 comments,
        string memory codeHash
    ) external nonReentrant {
        require(linesOfCode >= 10, "Minimum 10 lines required");
        require(block.timestamp >= lastClaimTime[msg.sender] + COOLDOWN, "Cooldown period not met");
        require(!usedCodeHashes[keccak256(bytes(codeHash))], "Code already submitted");

        // Reset daily limit if needed
        if (block.timestamp >= lastDayReset[msg.sender] + 1 days) {
            dailyClaimed[msg.sender] = 0;
            lastDayReset[msg.sender] = block.timestamp;
        }

        // Calculate reward
        uint256 reward = linesOfCode * BASE_RATE;
        reward += functions * BASE_RATE * 2;
        reward += classes * BASE_RATE * 3;
        reward += tests * BASE_RATE * 2;

        require(reward >= MIN_CLAIM, "Reward below minimum");
        require(dailyClaimed[msg.sender] + reward <= DAILY_LIMIT, "Daily limit exceeded");

        // Mark code as used
        usedCodeHashes[keccak256(bytes(codeHash))] = true;

        // Update state
        lastClaimTime[msg.sender] = block.timestamp;
        dailyClaimed[msg.sender] += reward;
        totalLinesSubmitted[msg.sender] += linesOfCode;
        totalRewardsEarned[msg.sender] += reward;
        totalClaims[msg.sender]++;

        // Transfer tokens
        CODE_TOKEN.transferFrom(REWARDS_POOL, msg.sender, reward);

        emit RewardClaimed(msg.sender, reward, linesOfCode, functions, classes, tests);
    }
}

